import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isAboutDropdownOpen, setIsAboutDropdownOpen] = useState(false);
  const [isProgramsDropdownOpen, setIsProgramsDropdownOpen] = useState(false);
  const [isGalleryDropdownOpen, setIsGalleryDropdownOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
    setIsAboutDropdownOpen(false);
    setIsProgramsDropdownOpen(false);
    setIsGalleryDropdownOpen(false);
  };

  const getMenuItems = (menuName) => {
    switch (menuName) {
      case 'ABOUT US':
        return aboutMenuItems;
      case 'PROGRAMS':
        return programsMenuItems;
      case 'GALLERY':
        return galleryMenuItems;
      default:
        return [];
    }
  };

  const getDropdownState = (menuName) => {
    switch (menuName) {
      case 'ABOUT US':
        return isAboutDropdownOpen;
      case 'PROGRAMS':
        return isProgramsDropdownOpen;
      case 'GALLERY':
        return isGalleryDropdownOpen;
      default:
        return false;
    }
  };

  const setDropdownState = (menuName, state) => {
    switch (menuName) {
      case 'ABOUT US':
        setIsAboutDropdownOpen(state);
        break;
      case 'PROGRAMS':
        setIsProgramsDropdownOpen(state);
        break;
      case 'GALLERY':
        setIsGalleryDropdownOpen(state);
        break;
      default:
        break;
    }
  };

  // About Us dropdown menu items
  const aboutMenuItems = [
    { name: 'Organization', path: '/about/organization' },
    { name: 'Vision Statement', path: '/about/vision' },
    { name: 'Mission Statement', path: '/about/mission' },
    { name: 'Core Values', path: '/about/values' },
    { name: 'Founder & Director', path: '/about/founder' },
    { name: 'Co-Founder', path: '/about/co-founder' }
  ];

  // Programs dropdown menu items
  const programsMenuItems = [
    { name: 'Corporate Training', path: '/programs/corporate' },
    { name: 'Training for Educational Institutions', path: '/programs/educational' },
    { name: 'Train the Trainer Certification', path: '/programs/certification' }
  ];

  // Gallery dropdown menu items
  const galleryMenuItems = [
    { name: 'Photos', path: '/gallery/photos' },
    { name: 'Videos', path: '/gallery/videos' }
  ];

  // Clean navigation items with dropdowns
  const navItems = [
    { name: 'HOME', path: '/' },
    { name: 'ABOUT US', path: '/about', hasDropdown: true },
    { name: 'PROGRAMS', path: '/programs', hasDropdown: true },
    { name: 'GALLERY', path: '/gallery', hasDropdown: true },
    { name: 'TESTIMONIALS', path: '/testimonials' },
    { name: 'CONTACT US', path: '/contact' }
  ];

  return (
    <header className="header">
      <div className="header-content">
        <div className="logo">
          <Link to="/" onClick={closeMenu}>
            <img src="/images/logo.png" alt="NPY Skills Academy" />
          </Link>
        </div>

        <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`} role="navigation" aria-label="Main navigation">
          <ul className="nav-list">
            {navItems.map((item) => (
              <li key={item.path} className="nav-item">
                {item.hasDropdown ? (
                  <div className="dropdown">
                    <div
                      className={`nav-link dropdown-toggle ${location.pathname.startsWith(item.path) ? 'active' : ''}`}
                      onMouseEnter={() => {
                        if (item.name === 'ABOUT US') setIsAboutDropdownOpen(true);
                        if (item.name === 'PROGRAMS') setIsProgramsDropdownOpen(true);
                      }}
                      onMouseLeave={() => {
                        if (item.name === 'ABOUT US') setIsAboutDropdownOpen(false);
                        if (item.name === 'PROGRAMS') setIsProgramsDropdownOpen(false);
                      }}
                      onClick={() => {
                        if (item.name === 'ABOUT US') setIsAboutDropdownOpen(!isAboutDropdownOpen);
                        if (item.name === 'PROGRAMS') setIsProgramsDropdownOpen(!isProgramsDropdownOpen);
                      }}
                      role="button"
                      aria-expanded={item.name === 'ABOUT US' ? isAboutDropdownOpen : isProgramsDropdownOpen}
                      aria-haspopup="true"
                    >
                      {item.name}
                      <span className="dropdown-arrow">â–¼</span>
                    </div>
                    <div
                      className={`dropdown-menu ${item.name === 'ABOUT US' ? (isAboutDropdownOpen ? 'show' : '') : (isProgramsDropdownOpen ? 'show' : '')}`}
                      onMouseEnter={() => {
                        if (item.name === 'ABOUT US') setIsAboutDropdownOpen(true);
                        if (item.name === 'PROGRAMS') setIsProgramsDropdownOpen(true);
                      }}
                      onMouseLeave={() => {
                        if (item.name === 'ABOUT US') setIsAboutDropdownOpen(false);
                        if (item.name === 'PROGRAMS') setIsProgramsDropdownOpen(false);
                      }}
                      role="menu"
                      aria-label={`${item.name} submenu`}
                    >
                      {(item.name === 'ABOUT US' ? aboutMenuItems : programsMenuItems).map((menuItem, index) => (
                        <Link
                          key={index}
                          to={menuItem.path}
                          className="dropdown-item"
                          onClick={closeMenu}
                          role="menuitem"
                        >
                          {menuItem.name}
                        </Link>
                      ))}
                    </div>
                  </div>
                ) : (
                  <Link
                    to={item.path}
                    className={`nav-link ${location.pathname === item.path ? 'active' : ''}`}
                    onClick={closeMenu}
                  >
                    {item.name}
                  </Link>
                )}
              </li>
            ))}
          </ul>
        </nav>

        <button
          className={`hamburger ${isMenuOpen ? 'hamburger-open' : ''}`}
          onClick={toggleMenu}
          aria-label="Toggle menu"
          aria-expanded={isMenuOpen}
        >
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
    </header>
  );
};

export default Header;
