import React, { useState, useEffect } from 'react';
import Organization from './Organization';
import './About.css';

const About = () => {
  const [activeAccordion, setActiveAccordion] = useState(null);

  useEffect(() => {
    const hash = window.location.hash;
    if (hash === '#vision') {
      setActiveAccordion(0);
    }
  }, []);

  const toggleAccordion = (index) => {
    setActiveAccordion(activeAccordion === index ? null : index);
  };

  const accordionData = [
    {
      title: 'Vision Statement',
      content: 'ðŸ”¹ Vision Statement\nTo be a globally recognized hub for transformational learning, empowering individuals and institutions to excel through skill development, innovative training, and human-centered growth.'
    }
  ];

  return (
    <div className="about">
      <section className="about-hero">
        <div className="container">
          <h1>About NPY Skills Academy</h1>
          <p>Discover our story, mission, and the passionate team behind transformative learning experiences.</p>
        </div>
      </section>

      <section className="about-content">
        <div className="container">
          <div className="accordion-container">
            {accordionData.map((item, index) => (
              <div key={index} className="accordion-item">
                <button
                  className={`accordion-header ${activeAccordion === index ? 'active' : ''}`}
                  onClick={() => toggleAccordion(index)}
                  aria-expanded={activeAccordion === index}
                  aria-controls={`accordion-content-${index}`}
                >
                  <span className="accordion-title">{item.title}</span>
                  <span className="accordion-icon">
                    {activeAccordion === index ? 'âˆ’' : '+'}
                  </span>
                </button>
                <div
                  id={`accordion-content-${index}`}
                  className={`accordion-content ${activeAccordion === index ? 'active' : ''}`}
                  aria-hidden={activeAccordion !== index}
                >
                  <div className="accordion-body">
                    {item.content}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Organization Section */}
      <Organization />
    </div>
  );
};

export default About;
