import React, { useState, useEffect, useRef } from 'react';
import './Footer.css';

const Footer = () => {
  const [showTooltip, setShowTooltip] = useState(null);
  const [email, setEmail] = useState('');
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [isVisible, setIsVisible] = useState(false);
  const observerRef = useRef(null);

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const handleSubscribe = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    if (!validateEmail(email)) {
      setError('Please enter a valid email address');
      setIsLoading(false);
      return;
    }

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1200));
      setIsSubscribed(true);
      setEmail('');
    } catch (err) {
      setError('Subscription failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-in');
            setIsVisible(true);
          }
        });
      },
      { threshold: 0.1 }
    );

    if (observerRef.current) {
      observer.observe(observerRef.current);
    }

    return () => observer.disconnect();
  }, []);




  const socialLinks = [
    { name: 'Facebook', icon: 'üìò', url: '#', color: '#1877f2' },
    { name: 'Twitter', icon: 'üê¶', url: '#', color: '#1da1f2' },
    { name: 'LinkedIn', icon: 'üíº', url: '#', color: '#0077b5' },
    { name: 'Instagram', icon: 'üì∑', url: '#', color: '#e4405f' }
  ];


  return (
    <footer className="footer" ref={observerRef}>
      <div className="container">
        <div className="footer-content">
          <div className="footer-section">
            <h3>NPY Skills Academy</h3>
            <p>Empowering individuals and organizations through transformative learning experiences since 2002.</p>
          </div>

          <div className="footer-section">
            <h3>Quick Links</h3>
            <div className="quick-links-row">
              <a href="/">Home</a>
              <a href="/about">About Us</a>
              <a href="/programs">Programs</a>
              <a href="/gallery">Gallery</a>
              <a href="/contact">Contact</a>
            </div>
          </div>

          <div className="footer-section">
            <h3>Contact Info</h3>
            <ul>
              <li>üìß npyskillsacademy@gmail.com</li>
              <li>üìû +91 98765 43210</li>
              <li>üìç No. 48/25, 1st Main, Marenahalli, Vijaynagar, Bangalore - 560040</li>
            </ul>
          </div>

          <div className="footer-section">
            <h3>Follow Us</h3>
            <div className="social-links">
              {socialLinks.map((social, index) => (
                <div key={social.name} className="social-icon-wrapper">
                  <a
                    href={social.url}
                    aria-label={`Follow us on ${social.name}`}
                    onMouseEnter={() => setShowTooltip(index)}
                    onMouseLeave={() => setShowTooltip(null)}
                    onFocus={() => setShowTooltip(index)}
                    onBlur={() => setShowTooltip(null)}
                    className="social-icon"
                    style={{ '--hover-color': social.color }}
                  >
                    {social.icon}
                  </a>
                  {showTooltip === index && (
                    <div className="tooltip" role="tooltip">
                      {social.name}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="footer-bottom">
          <p>&copy; 2025 NPY Skills Academy. All rights reserved.</p>
          <div className="footer-accent"></div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
