import React, { useState, useEffect } from 'react';
import Organization from './Organization';
import './About.css';

const About = () => {
  const [activeAccordion, setActiveAccordion] = useState(null);

  useEffect(() => {
    const hash = window.location.hash;
    if (hash === '#vision') {
      setActiveAccordion(0);
    }
  }, []);

  const toggleAccordion = (index) => {
    setActiveAccordion(activeAccordion === index ? null : index);
  };

  const accordionData = [
    {
      title: 'Vision Statement',
      content: 'ðŸ”¹ Vision Statement\nTo be a globally recognized hub for transformational learning, empowering individuals and institutions to excel through skill development, innovative training, and human-centered growth.'
    }
  ];

  return (
    <div className="about">
      <section className="about-hero">
        <div className="container">
          <h1>About NPY Skills Academy</h1>
          <p>Discover our story, mission, and the passionate team behind transformative learning experiences.</p>
        </div>
      </section>

      <section className="about-content">
        <div className="container">
          <div className="vision-card">
            <h2>Vision Statement</h2>
            <div className="vision-content">
              <p>ðŸ”¹ Vision Statement</p>
              <p>To be a globally recognized hub for transformational learning, empowering individuals and institutions to excel through skill development, innovative training, and human-centered growth.</p>
            </div>
          </div>
        </div>
      </section>

      {/* Organization Section */}
      <Organization />
    </div>
  );
};

export default About;
