import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isAboutDropdownOpen, setIsAboutDropdownOpen] = useState(false);
  const [isProgramsDropdownOpen, setIsProgramsDropdownOpen] = useState(false);
  const [isGalleryDropdownOpen, setIsGalleryDropdownOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
    setIsAboutDropdownOpen(false);
  };

  const aboutMenuItems = [
    { title: 'Organization'},
    { title: 'Vision Statement'},
    { title: 'Mission Statement' },
    { title: 'Core Values'},
    { title: 'Founder & Director'},
    { title: 'Co-Founder'}
  ];

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <Link to="/" onClick={closeMenu}>
              <img src="/images/logo.png" alt="NPY Skills Academy" />
            </Link>
          </div>

          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
            <ul className="nav-list">
              <li className="nav-item">
                <Link
                  to="/"
                  className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Home
                </Link>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/about' ? 'active' : ''}`}
                  onMouseEnter={() => setIsAboutDropdownOpen(true)}
                  onMouseLeave={() => setIsAboutDropdownOpen(false)}
                >
                  About Us
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isAboutDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsAboutDropdownOpen(true)}
                  onMouseLeave={() => setIsAboutDropdownOpen(false)}
                >
                  {aboutMenuItems.map((item, index) => (
                    <div key={index} className="dropdown-item">
                      <div className="dropdown-item-title">{item.title}</div>
                      <div className="dropdown-item-description">{item.description}</div>
                    </div>
                  ))}
                </div>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/programs' ? 'active' : ''}`}
                  onMouseEnter={() => setIsProgramsDropdownOpen(true)}
                  onMouseLeave={() => setIsProgramsDropdownOpen(false)}
                >
                  Programs
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isProgramsDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsProgramsDropdownOpen(true)}
                  onMouseLeave={() => setIsProgramsDropdownOpen(false)}
                >
                  <div className="dropdown-item">
                    <div className="dropdown-item-title">Corporate Training</div>
                    </div>
                  <div className="dropdown-item">
                    <div className="dropdown-item-title">Training for Educational Institutions</div>
                                      </div>
                  <div className="dropdown-item">
                    <div className="dropdown-item-title">Train the Trainer Certification</div>
                                      </div>
                </div>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/gallery' ? 'active' : ''}`}
                  onMouseEnter={() => setIsGalleryDropdownOpen(true)}
                  onMouseLeave={() => setIsGalleryDropdownOpen(false)}
                >
                  Gallery
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isGalleryDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsGalleryDropdownOpen(true)}
                  onMouseLeave={() => setIsGalleryDropdownOpen(false)}
                >
                  <div className="dropdown-item">
                    <div className="dropdown-item-title">Photos</div>
                                      </div>
                  <div className="dropdown-item">
                    <div className="dropdown-item-title">Videos</div>
                    <div className="dropdown-item-description">Watch video testimonials and program highlights</div>
                  </div>
                </div>
              </li>
              <li className="nav-item">
                <Link
                  to="/testimonials"
                  className={`nav-link ${location.pathname === '/testimonials' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Testimonials
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="/contact"
                  className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Contact
                </Link>
              </li>
            </ul>
          </nav>

          <button
            className={`hamburger ${isMenuOpen ? 'hamburger-open' : ''}`}
            onClick={toggleMenu}
            aria-label="Toggle menu"
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
