import React, { useState, useEffect, useRef } from 'react';
import './Footer.css';

const Footer = () => {
  const observerRef = useRef(null);
  const [email, setEmail] = useState('');
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-in');
          }
        });
      },
      { threshold: 0.1 }
    );

    if (observerRef.current) {
      observer.observe(observerRef.current);
    }

    return () => observer.disconnect();
  }, []);

  const handleSubscribe = async (e) => {
    e.preventDefault();
    if (!email) {
      setError('Please enter your email address');
      return;
    }

    setIsLoading(true);
    setError('');

    // Simulate API call
    setTimeout(() => {
      setIsSubscribed(true);
      setIsLoading(false);
      setEmail('');
    }, 2000);
  };

  const socialLinks = [
    { name: 'Facebook', icon: 'üìò', url: '#', color: '#1877f2', hoverColor: '#166fe5' },
    { name: 'Twitter', icon: 'üê¶', url: '#', color: '#1da1f2', hoverColor: '#1a91da' },
    { name: 'LinkedIn', icon: 'üíº', url: '#', color: '#0077b5', hoverColor: '#005885' },
    { name: 'Instagram', icon: 'üì∑', url: '#', color: '#e4405f', hoverColor: '#d6336c' },
    { name: 'YouTube', icon: 'üì∫', url: '#', color: '#ff0000', hoverColor: '#cc0000' }
  ];






  return (
    <footer className="footer" ref={observerRef}>
      <div className="container">
        <div className="footer-content">
          <div className="footer-section">
            <h3>NPY Skills Academy</h3>
            <p>Empowering individuals and organizations through transformative learning experiences since 2002.</p>
            <div className="social-links">
              {socialLinks.map((social, index) => (
                <div key={social.name} className="social-icon-wrapper">
                  <a
                    href={social.url}
                    className="social-icon"
                    style={{
                      '--hover-color': social.color,
                      '--hover-color-alt': social.hoverColor
                    }}
                    aria-label={`Follow us on ${social.name} - opens in new tab`}
                    title={social.name}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {social.icon}
                  </a>
                  <div className="tooltip" role="tooltip" aria-hidden="true">{social.name}</div>
                </div>
              ))}
            </div>
          </div>

          <div className="footer-section">
            <h3>Quick Links</h3>
            <div className="quick-links-row">
              <a href="/">Home</a>
              <a href="/about">About Us</a>
              <a href="/programs">Programs</a>
              <a href="/gallery">Gallery</a>
              <a href="/contact">Contact</a>
            </div>
          </div>

          <div className="footer-section">
            <h3>Contact Info</h3>
            <ul>
              <li>üìß npyskillsacademy@gmail.com</li>
              <li>üìû +91 98765 43210</li>
              <li>üìç No. 48/25, 1st Main, Marenahalli, Vijaynagar, Bangalore - 560040</li>
            </ul>
            <div className="newsletter-section">
              <h4>Stay Connected</h4>
              {!isSubscribed ? (
                <form onSubmit={handleSubscribe} className="newsletter-form">
                  <div className="input-group">
                    <input
                      type="email"
                      placeholder="Enter your email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      className={error ? 'error' : ''}
                      disabled={isLoading}
                    />
                    <button type="submit" className="subscribe-btn" disabled={isLoading}>
                      {isLoading ? (
                        <div className="loading-dots">
                          <span></span>
                          <span></span>
                          <span></span>
                        </div>
                      ) : (
                        'Subscribe'
                      )}
                    </button>
                  </div>
                  {error && <div className="error-message">{error}</div>}
                </form>
              ) : (
                <div className="success-message">
                  <span className="success-icon">‚úÖ</span>
                  <p>Thank you for subscribing! Check your email for confirmation.</p>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="footer-bottom">
          <p>&copy; 2025 NPY Skills Academy. All rights reserved.</p>
          <div className="footer-accent"></div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
