import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isAboutDropdownOpen, setIsAboutDropdownOpen] = useState(false);
  const [isProgramsDropdownOpen, setIsProgramsDropdownOpen] = useState(false);
  const [isGalleryDropdownOpen, setIsGalleryDropdownOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
    setIsAboutDropdownOpen(false);
    setIsProgramsDropdownOpen(false);
    setIsGalleryDropdownOpen(false);
  };

  // Simplified navigation - no dropdowns for clean header
  const navItems = [
    { name: 'HOME', path: '/' },
    { name: 'ABOUT US', path: '/about' },
    { name: 'PROGRAMS', path: '/programs' },
    { name: 'GALLERY', path: '/gallery' },
    { name: 'CONTACT US', path: '/contact' },
    { name: 'DOWNLOADS', path: '/downloads' }
  ];

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <Link to="/" onClick={closeMenu}>
              <img src="/images/logo.png" alt="NPY Skills Academy" />
            </Link>
          </div>

          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`} role="navigation" aria-label="Main navigation">
            <ul className="nav-list">
              <li className="nav-item">
                <Link
                  to="/"
                  className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Home
                </Link>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/about' ? 'active' : ''}`}
                  onMouseEnter={() => setIsAboutDropdownOpen(true)}
                  onMouseLeave={() => setIsAboutDropdownOpen(false)}
                >
                  About Us
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isAboutDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsAboutDropdownOpen(true)}
                  onMouseLeave={() => setIsAboutDropdownOpen(false)}
                  role="menu"
                  aria-label="About Us submenu"
                >
                  {aboutMenuItems.map((item, index) => (
                    <div key={index} className="dropdown-item" role="menuitem">
                      {item}
                    </div>
                  ))}
                </div>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/programs' ? 'active' : ''}`}
                  onMouseEnter={() => setIsProgramsDropdownOpen(true)}
                  onMouseLeave={() => setIsProgramsDropdownOpen(false)}
                >
                  Programs
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isProgramsDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsProgramsDropdownOpen(true)}
                  onMouseLeave={() => setIsProgramsDropdownOpen(false)}
                  role="menu"
                  aria-label="Programs submenu"
                >
                  {programsMenuItems.map((item, index) => (
                    <div key={index} className="dropdown-item" role="menuitem">
                      {item}
                    </div>
                  ))}
                </div>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/gallery' ? 'active' : ''}`}
                  onMouseEnter={() => setIsGalleryDropdownOpen(true)}
                  onMouseLeave={() => setIsGalleryDropdownOpen(false)}
                >
                  Gallery
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isGalleryDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsGalleryDropdownOpen(true)}
                  onMouseLeave={() => setIsGalleryDropdownOpen(false)}
                  role="menu"
                  aria-label="Gallery submenu"
                >
                  {galleryMenuItems.map((item, index) => (
                    <div key={index} className="dropdown-item" role="menuitem">
                      {item}
                    </div>
                  ))}
                </div>
              </li>
              <li className="nav-item">
                <Link
                  to="/testimonials"
                  className={`nav-link ${location.pathname === '/testimonials' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Testimonials
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="/contact"
                  className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Contact
                </Link>
              </li>
            </ul>
          </nav>

          <button
            className={`hamburger ${isMenuOpen ? 'hamburger-open' : ''}`}
            onClick={toggleMenu}
            aria-label="Toggle menu"
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
