import React from 'react';
import './About.css';

const About = () => {
  const teamMembers = [
    {
      name: 'Dr. Naveen P Y',
      role: 'Founder & Director',
      bio: 'Dr. Naveen P Y is a distinguished educator and leadership development expert with over 15 years of experience in training and organizational development. He holds multiple certifications in leadership, outbound training, and corporate training methodologies.',
      image: '/images/founder.jpg'
    }
  ];

  const coreValues = [
    {
      title: 'Excellence',
      description: 'We strive for the highest standards in everything we do, from program design to delivery.'
    },
    {
      title: 'Integrity',
      description: 'We conduct our business with honesty, transparency, and ethical practices.'
    },
    {
      title: 'Innovation',
      description: 'We continuously evolve our methods to incorporate the latest in educational technology and pedagogy.'
    },
    {
      title: 'Empowerment',
      description: 'We empower individuals and organizations to reach their full potential through transformative learning.'
    },
    {
      title: 'Collaboration',
      description: 'We believe in the power of collaboration and partnership for mutual growth and success.'
    },
    {
      title: 'Authenticity',
      description: 'We promote genuine learning experiences that foster authentic personal and professional development.'
    },
    {
      title: 'Impact',
      description: 'Our success is measured by the lasting positive change we create in individuals and organizations.'
    }
  ];

  return (
    <div className="about">
      <section className="about-hero">
        <div className="container">
          <h1>About NPY Skills Academy</h1>
          <p>Discover our story, mission, and the passionate team behind transformative learning experiences.</p>
        </div>
      </section>

      <section className="mission-section">
        <div className="container">
          <div className="mission-content">
            <h2>Our Mission</h2>
            <p>
              At NPY Skills Academy, we believe in empowering individuals and organizations through
              innovative learning experiences that combine theoretical knowledge with practical application.
              Founded in 2002, we have been pioneers in experiential education, helping professionals
              and organizations achieve their full potential.
            </p>
            <p>
              Our unique methodology integrates outdoor adventures, classroom learning, and real-world
              projects to create lasting transformation. We have successfully conducted over 500 programs
              and touched the lives of more than 10,000 individuals across various sectors.
            </p>
          </div>
        </div>
      </section>

      <section className="team-section">
        <div className="container">
          <h2>Meet Our Team</h2>
          <div className="team-grid">
            {teamMembers.map((member, index) => (
              <div key={index} className="team-member">
                <img src={member.image} alt={member.name} />
                <h3>{member.name}</h3>
                <p className="role">{member.role}</p>
                <p className="bio">{member.bio}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="values-section">
        <div className="container">
          <h2>Our Core Values</h2>
          <div className="values-grid">
            {coreValues.map((value, index) => (
              <div key={index} className="value-card">
                <h3>{value.title}</h3>
                <p>{value.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default About;
