import React, { useState, useRef } from 'react';
import { motion, useInView } from 'framer-motion';
import './Contact.css';

/**
 * Contact Component - Interactive contact form and information page
 * Features: Animated sections, form validation, accessibility compliance, responsive design
 */
const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    organization: '',
    program: '',
    message: ''
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  // Animation refs and states
  const heroRef = useRef(null);
  const isHeroInView = useInView(heroRef, { once: true });

  // Animation variants for Framer Motion
  const heroVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: {
      opacity: 1,
      scale: 1,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.3
      }
    }
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.message.trim()) {
      newErrors.message = 'Message is required';
    } else if (formData.message.trim().length < 10) {
      newErrors.message = 'Message must be at least 10 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    // Simulate form submission
    try {
      await new Promise(resolve => setTimeout(resolve, 2000));
      setSubmitMessage('Thank you for your message! We will get back to you within 24 hours.');
      setFormData({
        name: '',
        email: '',
        phone: '',
        organization: '',
        program: '',
        message: ''
      });
    } catch (error) {
      setSubmitMessage('There was an error sending your message. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="contact">
      <motion.section
        ref={heroRef}
        className="contact-hero"
        variants={heroVariants}
        initial="hidden"
        animate={isHeroInView ? "visible" : "hidden"}
        aria-labelledby="contact-hero-heading"
      >
        <div className="container">
          <motion.h1
            id="contact-hero-heading"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Contact Us
          </motion.h1>
          <motion.p
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Get in touch to learn more about our programs and how we can help you or your organization grow.
          </motion.p>
        </div>
      </motion.section>

      <motion.section
        className="contact-content"
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, margin: "-100px" }}
      >
        <div className="container">
          <div className="contact-grid">
            <motion.div
              className="contact-info"
              variants={cardVariants}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              tabIndex={0}
              role="region"
              aria-labelledby="contact-info-heading"
            >
              <h2 id="contact-info-heading">Get In Touch</h2>
              <div className="contact-details">
                <motion.div
                  className="contact-item"
                  whileHover={{ scale: 1.05, x: 10 }}
                  transition={{ type: "spring", stiffness: 300 }}
                >
                  <h3>Address</h3>
                  <p>
                    NPY Skills Academy<br />
                    No. 48/25, 1st Main, Marenahalli, Vijaynagar<br />
                    Bangalore - 560040<br />
                    Karnataka, India
                  </p>
                </motion.div>
                <motion.div
                  className="contact-item"
                  whileHover={{ scale: 1.05, x: 10 }}
                  transition={{ type: "spring", stiffness: 300 }}
                >
                  <h3>Phone</h3>
                  <p>+91 98765 43210</p>
                </motion.div>
                <motion.div
                  className="contact-item"
                  whileHover={{ scale: 1.05, x: 10 }}
                  transition={{ type: "spring", stiffness: 300 }}
                >
                  <h3>Email</h3>
                  <p>npyskillsacademy@gmail.com</p>
                </motion.div>
                <motion.div
                  className="contact-item"
                  whileHover={{ scale: 1.05, x: 10 }}
                  transition={{ type: "spring", stiffness: 300 }}
                >
                  <h3>Business Hours</h3>
                  <p>
                    Monday - Friday: 9:00 AM - 6:00 PM<br />
                    Saturday: 9:00 AM - 2:00 PM<br />
                    Sunday: Closed
                  </p>
                </motion.div>
              </div>

              <motion.div
                className="map-container"
                whileHover={{ scale: 1.02 }}
                transition={{ duration: 0.3 }}
              >
                <h3>Find Us</h3>
                <div className="map-grid">
                  <motion.div
                    className="map-item"
                    initial={{ opacity: 0, x: -50 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.6, delay: 0.1 }}
                    viewport={{ once: true }}
                  >
                    <h4>Main Campus</h4>
                    <motion.iframe
                      src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3888.001!2d77.5946!3d12.9716!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3bae167e1b7d4b0f%3A0x4b5c6e3f8d9e2a1b!2sNo.%2048%2F25%2C%201st%20Main%2C%20Marenahalli%2C%20Vijaynagar%2C%20Bengaluru%2C%20Karnataka%20560040!5e0!3m2!1sen!2sin!4v1634567890123!5m2!1sen!2sin"
                      width="100%"
                      height="250"
                      style={{ border: 0 }}
                      allowFullScreen=""
                      loading="lazy"
                      title="NPY Skills Academy Main Campus"
                      whileHover={{ scale: 1.02 }}
                      transition={{ duration: 0.3 }}
                    />
                    <div className="map-details">
                      <p><strong>Address:</strong> No. 48/25, 1st Main, Marenahalli, Vijaynagar</p>
                      <p><strong>City:</strong> Bangalore - 560040, Karnataka, India</p>
                      <p><strong>Landmark:</strong> Near Vijaynagar Metro Station</p>
                    </div>
                  </motion.div>

                  <motion.div
                    className="map-item"
                    initial={{ opacity: 0, x: 50 }}
                    whileInView={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.6, delay: 0.3 }}
                    viewport={{ once: true }}
                  >
                    <h4>Training Center</h4>
                    <motion.iframe
                      src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3887.8!2d77.58!3d12.97!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3bae1670e1b7d4b0f%3A0x4b5c6e3f8d9e2a1c!2sRajajinagar%2C%20Bengaluru%2C%20Karnataka!5e0!3m2!1sen!2sin!4v1634567890124!5m2!1sen!2sin"
                      width="100%"
                      height="250"
                      style={{ border: 0 }}
                      allowFullScreen=""
                      loading="lazy"
                      title="NPY Skills Academy Training Center"
                      whileHover={{ scale: 1.02 }}
                      transition={{ duration: 0.3 }}
                    />
                    <div className="map-details">
                      <p><strong>Address:</strong> Rajajinagar, Bangalore</p>
                      <p><strong>City:</strong> Bangalore - 560010, Karnataka, India</p>
                      <p><strong>Facilities:</strong> Advanced Training Labs & Workshops</p>
                    </div>
                  </motion.div>
                </div>
              </motion.div>
            </motion.div>

            <motion.div
              className="contact-form"
              variants={cardVariants}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              tabIndex={0}
              role="region"
              aria-labelledby="contact-form-heading"
            >
              <h2 id="contact-form-heading">Send us a Message</h2>
              {submitMessage && (
                <motion.div
                  id="submit-message"
                  className={`submit-message ${submitMessage.includes('error') ? 'error' : 'success'}`}
                  role="alert"
                  aria-live="polite"
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                >
                  {submitMessage}
                </motion.div>
              )}

              <form onSubmit={handleSubmit}>
                <motion.div
                  className="form-group"
                  whileHover={{ scale: 1.01 }}
                  transition={{ duration: 0.2 }}
                >
                  <label htmlFor="name">Full Name *</label>
                  <motion.input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    className={errors.name ? 'error' : ''}
                    required
                    whileFocus={{ scale: 1.02, borderColor: "#667eea" }}
                    transition={{ duration: 0.2 }}
                  />
                  {errors.name && (
                    <motion.span
                      className="error-text"
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ duration: 0.3 }}
                    >
                      {errors.name}
                    </motion.span>
                  )}
                </motion.div>

                <motion.div
                  className="form-group"
                  whileHover={{ scale: 1.01 }}
                  transition={{ duration: 0.2 }}
                >
                  <label htmlFor="email">Email Address *</label>
                  <motion.input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    className={errors.email ? 'error' : ''}
                    required
                    whileFocus={{ scale: 1.02, borderColor: "#667eea" }}
                    transition={{ duration: 0.2 }}
                  />
                  {errors.email && (
                    <motion.span
                      className="error-text"
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ duration: 0.3 }}
                    >
                      {errors.email}
                    </motion.span>
                  )}
                </motion.div>

                <motion.div
                  className="form-group"
                  whileHover={{ scale: 1.01 }}
                  transition={{ duration: 0.2 }}
                >
                  <label htmlFor="phone">Phone Number</label>
                  <motion.input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    whileFocus={{ scale: 1.02, borderColor: "#667eea" }}
                    transition={{ duration: 0.2 }}
                  />
                </motion.div>

                <motion.div
                  className="form-group"
                  whileHover={{ scale: 1.01 }}
                  transition={{ duration: 0.2 }}
                >
                  <label htmlFor="organization">Organization</label>
                  <motion.input
                    type="text"
                    id="organization"
                    name="organization"
                    value={formData.organization}
                    onChange={handleChange}
                    whileFocus={{ scale: 1.02, borderColor: "#667eea" }}
                    transition={{ duration: 0.2 }}
                  />
                </motion.div>

                <motion.div
                  className="form-group"
                  whileHover={{ scale: 1.01 }}
                  transition={{ duration: 0.2 }}
                >
                  <label htmlFor="program">Program of Interest</label>
                  <motion.select
                    id="program"
                    name="program"
                    value={formData.program}
                    onChange={handleChange}
                    whileFocus={{ scale: 1.02, borderColor: "#667eea" }}
                    transition={{ duration: 0.2 }}
                  >
                    <option value="">Select a program</option>
                    <option value="corporate">Corporate Training</option>
                    <option value="education">Training for Educational Institutions</option>
                    <option value="certification">Train the Trainer Certification</option>
                    <option value="other">Other</option>
                  </motion.select>
                </motion.div>

                <motion.div
                  className="form-group"
                  whileHover={{ scale: 1.01 }}
                  transition={{ duration: 0.2 }}
                >
                  <label htmlFor="message">Message *</label>
                  <motion.textarea
                    id="message"
                    name="message"
                    rows="5"
                    value={formData.message}
                    onChange={handleChange}
                    className={errors.message ? 'error' : ''}
                    required
                    whileFocus={{ scale: 1.02, borderColor: "#667eea" }}
                    transition={{ duration: 0.2 }}
                  />
                  {errors.message && (
                    <motion.span
                      className="error-text"
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ duration: 0.3 }}
                    >
                      {errors.message}
                    </motion.span>
                  )}
                </motion.div>

                <motion.button
                  type="submit"
                  className="btn btn-primary"
                  disabled={isSubmitting}
                  aria-describedby={submitMessage ? "submit-message" : undefined}
                  whileHover={{ scale: 1.05, backgroundColor: "#5a67d8" }}
                  whileTap={{ scale: 0.95 }}
                  transition={{ duration: 0.2 }}
                >
                  {isSubmitting ? 'Sending...' : 'Send Message'}
                </motion.button>
              </form>
            </motion.div>
          </div>
        </div>
      </motion.section>
    </div>
  );
};

export default Contact;
