import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isAboutDropdownOpen, setIsAboutDropdownOpen] = useState(false);
  const [isProgramsDropdownOpen, setIsProgramsDropdownOpen] = useState(false);
  const [isGalleryDropdownOpen, setIsGalleryDropdownOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
    setIsAboutDropdownOpen(false);
  };

  const aboutMenuItems = [
    { title: 'Organization', description: 'Learn about NPY Skills Academy and our commitment to excellence' },
    { title: 'Vision Statement', description: 'Our vision for transforming education and skill development' },
    { title: 'Mission Statement', description: 'Our mission to empower individuals and organizations' },
    { title: 'Core Values', description: 'The principles that guide our work and decisions' },
    { title: 'Founder & Director', description: 'Meet Dr. Naveen P Y, our visionary leader and founder' },
    { title: 'Co-Founder', description: 'Learn about our co-founder and their contributions' }
  ];

  const programsMenuItems = [
    { title: 'Corporate Training', description: 'Leadership, team building, communication, conflict resolution, and more' },
    { title: 'Training for Educational Institutions', description: 'Faculty workshops and student soft skills, personality development, and career readiness' },
    { title: 'Train the Trainer Certification', description: 'TTT, Outbound, Corporate, Soft Skills, and Gamification Certifications' }
  ];

  const galleryMenuItems = [
    { title: 'Photos', description: 'View our photo gallery showcasing training sessions and events' },
    { title: 'Videos', description: 'Watch video testimonials and program highlights' }
  ];

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <Link to="/" onClick={closeMenu}>
              <img src="/images/logo.png" alt="NPY Skills Academy" />
            </Link>
          </div>

          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
            <ul className="nav-list">
              <li className="nav-item">
                <Link
                  to="/"
                  className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Home
                </Link>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/about' ? 'active' : ''}`}
                  onMouseEnter={() => setIsAboutDropdownOpen(true)}
                  onMouseLeave={() => setIsAboutDropdownOpen(false)}
                >
                  About Us
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isAboutDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsAboutDropdownOpen(true)}
                  onMouseLeave={() => setIsAboutDropdownOpen(false)}
                >
                  {aboutMenuItems.map((item, index) => (
                    <div key={index} className="dropdown-item">
                      <div className="dropdown-item-title">{item.title}</div>
                      <div className="dropdown-item-description">{item.description}</div>
                    </div>
                  ))}
                </div>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/programs' ? 'active' : ''}`}
                  onMouseEnter={() => setIsProgramsDropdownOpen(true)}
                  onMouseLeave={() => setIsProgramsDropdownOpen(false)}
                >
                  Programs
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isProgramsDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsProgramsDropdownOpen(true)}
                  onMouseLeave={() => setIsProgramsDropdownOpen(false)}
                >
                  {programsMenuItems.map((item, index) => (
                    <div key={index} className="dropdown-item">
                      <div className="dropdown-item-title">{item.title}</div>
                      <div className="dropdown-item-description">{item.description}</div>
                    </div>
                  ))}
                </div>
              </li>
              <li className="nav-item dropdown">
                <div
                  className={`nav-link dropdown-toggle ${location.pathname === '/gallery' ? 'active' : ''}`}
                  onMouseEnter={() => setIsGalleryDropdownOpen(true)}
                  onMouseLeave={() => setIsGalleryDropdownOpen(false)}
                >
                  Gallery
                  <span className="dropdown-arrow">▼</span>
                </div>
                <div
                  className={`dropdown-menu ${isGalleryDropdownOpen ? 'show' : ''}`}
                  onMouseEnter={() => setIsGalleryDropdownOpen(true)}
                  onMouseLeave={() => setIsGalleryDropdownOpen(false)}
                >
                  {galleryMenuItems.map((item, index) => (
                    <div key={index} className="dropdown-item">
                      <div className="dropdown-item-title">{item.title}</div>
                      <div className="dropdown-item-description">{item.description}</div>
                    </div>
                  ))}
                </div>
              </li>
              <li className="nav-item">
                <Link
                  to="/testimonials"
                  className={`nav-link ${location.pathname === '/testimonials' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Testimonials
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="/contact"
                  className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`}
                  onClick={closeMenu}
                >
                  Contact
                </Link>
              </li>
            </ul>
          </nav>

          <button
            className={`hamburger ${isMenuOpen ? 'hamburger-open' : ''}`}
            onClick={toggleMenu}
            aria-label="Toggle menu"
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
