import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isAboutDropdownOpen, setIsAboutDropdownOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
    setIsAboutDropdownOpen(false);
  };

  // About Us dropdown menu items
  const aboutMenuItems = [
    { name: 'Organization', path: '/about/organization' },
    { name: 'Vision Statement', path: '/about/vision' },
    { name: 'Mission Statement', path: '/about/mission' },
    { name: 'Core Values', path: '/about/values' },
    { name: 'Founder & Director', path: '/about/founder' },
    { name: 'Co-Founder', path: '/about/co-founder' }
  ];

  // Clean navigation items with About Us dropdown
  const navItems = [
    { name: 'HOME', path: '/' },
    { name: 'ABOUT US', path: '/about', hasDropdown: true },
    { name: 'PROGRAMS', path: '/programs' },
    { name: 'GALLERY', path: '/gallery' },
    { name: 'TESTIMONIALS', path: '/testimonials' },
    { name: 'CONTACT US', path: '/contact' }
  ];

  return (
    <header className="header">
      <div className="header-content">
        <div className="logo">
          <Link to="/" onClick={closeMenu}>
            <img src="/images/logo.png" alt="NPY Skills Academy" />
          </Link>
        </div>

        <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`} role="navigation" aria-label="Main navigation">
          <ul className="nav-list">
            {navItems.map((item) => (
              <li key={item.path} className="nav-item">
                {item.hasDropdown ? (
                  <div className="dropdown">
                    <div
                      className={`nav-link dropdown-toggle ${location.pathname.startsWith(item.path) ? 'active' : ''}`}
                      onMouseEnter={() => setIsAboutDropdownOpen(true)}
                      onMouseLeave={() => setIsAboutDropdownOpen(false)}
                      onClick={() => setIsAboutDropdownOpen(!isAboutDropdownOpen)}
                      role="button"
                      aria-expanded={isAboutDropdownOpen}
                      aria-haspopup="true"
                    >
                      {item.name}
                      <span className="dropdown-arrow">â–¼</span>
                    </div>
                    <div
                      className={`dropdown-menu ${isAboutDropdownOpen ? 'show' : ''}`}
                      onMouseEnter={() => setIsAboutDropdownOpen(true)}
                      onMouseLeave={() => setIsAboutDropdownOpen(false)}
                      role="menu"
                      aria-label="About Us submenu"
                    >
                      {aboutMenuItems.map((menuItem, index) => (
                        <Link
                          key={index}
                          to={menuItem.path}
                          className="dropdown-item"
                          onClick={closeMenu}
                          role="menuitem"
                        >
                          {menuItem.name}
                        </Link>
                      ))}
                    </div>
                  </div>
                ) : (
                  <Link
                    to={item.path}
                    className={`nav-link ${location.pathname === item.path ? 'active' : ''}`}
                    onClick={closeMenu}
                  >
                    {item.name}
                  </Link>
                )}
              </li>
            ))}
          </ul>
        </nav>

        <button
          className={`hamburger ${isMenuOpen ? 'hamburger-open' : ''}`}
          onClick={toggleMenu}
          aria-label="Toggle menu"
          aria-expanded={isMenuOpen}
        >
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
    </header>
  );
};

export default Header;
